root.script:
function onInit()
    EditorManager.addEditor(
        "category_attack",
        {
            value="DMGTYPE",
            labelres="editor_dmgtype",
            windowclass="editor_DMGTYPE"
        }
    )
    EditorManager.addEditor(
        "category_spell",
        {
            value="DMGSTYPE",
            labelres="editor_dmgstype",
            windowclass="editor_DMGTYPE"
        }
    )
end
